import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { setPersona, setShowNameModal } from '../store/uiSlice';

const PersonaSelector = () => {
  const dispatch = useDispatch();
  const [selected, setSelected] = useState(null);

  const handlePersonaSelect = (persona) => {
    setSelected(persona);
  };

  const handleContinue = () => {
    if (selected) {
      dispatch(setPersona(selected));
      if (selected === 'student') {
        dispatch(setShowNameModal(true));
      }
    }
  };

  return (
    <div className="landing-page">
      <header className="header">
        <div className="header-content">
          <div className="logo">Intervue Poll</div>
        </div>
      </header>
      <main className="centered-page" style={{ minHeight: 'calc(100vh - 80px)' }}>
        <div className="card" style={{ width: 600, maxWidth: '95vw', textAlign: 'center', boxShadow: '0 2px 16px rgba(119, 101, 218, 0.08)' }}>
          <div className="badge" style={{ marginBottom: 24, marginTop: 8 }}>Intervue Poll</div>
          <h1 style={{ fontWeight: 500, fontSize: '2.2rem', marginBottom: 8 }}>
            Welcome to the <span style={{ fontWeight: 800 }}>Live Polling System</span>
          </h1>
          <div className="subtitle" style={{ marginBottom: 32, color: '#6E6E6E', fontSize: '1.1rem', fontWeight: 400 }}>
            Please select the role that best describes you to begin using the live polling system
          </div>
          <div className="role-cards" style={{ gap: 24, marginBottom: 32, justifyContent: 'center' }}>
            <div
              className={`role-card${selected === 'student' ? ' selected' : ''}`}
              tabIndex={0}
              onClick={() => handlePersonaSelect('student')}
              onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && handlePersonaSelect('student')}
              aria-label="I'm a Student"
              style={{ minHeight: 100 }}
            >
              <div className="role-card-title">I'm a Student</div>
              <div className="role-card-desc">
                Lorem Ipsum is simply dummy text of the printing and typesetting industry
              </div>
            </div>
            <div
              className={`role-card${selected === 'teacher' ? ' selected' : ''}`}
              tabIndex={0}
              onClick={() => handlePersonaSelect('teacher')}
              onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && handlePersonaSelect('teacher')}
              aria-label="I'm a Teacher"
              style={{ minHeight: 100 }}
            >
              <div className="role-card-title">I'm a Teacher</div>
              <div className="role-card-desc">
                Submit answers and view live poll results in real-time.
              </div>
            </div>
          </div>
          <button
            className="button-primary mt-2"
            style={{ width: 180 }}
            onClick={handleContinue}
            disabled={!selected}
          >
            Continue
          </button>
        </div>
      </main>
    </div>
  );
};

export default PersonaSelector;
